import { useState } from "react";
import CameraCapture from "./CameraCapture"
import "./style.css";

// 使用するGLSLフィルターのインデックス
export let shaderIndex = 0;

// Appコンポーネントの定義
function App() {
  // 次のシェーダをフィルターとして適用する
  function nextShader() {
    shaderIndex++;
  }

  // 前のシェーダをフィルターとして適用する
  function backShader() {
    shaderIndex--;
  }

  return (
    <div className="App">
      {/* ページのタイトル部分 */}
      <header>
        <h1>Capture Sketch</h1>
      </header>

      <main>
        {/* カメラのキャプチャ */}
        <CameraCapture shaderIndex={shaderIndex} />

        {/* UI */}
        <button onClick={backShader}>←</button>
        <button>o</button>
        <button onClick={nextShader}>→</button>
      </main>

    </div>
  );
}

// Appコンポーネントを他のファイルで利用できるようにエクスポート
export default App;
