import React, { useEffect } from "react";
import p5 from 'p5';

// 描画処理
const sketch = (p) => {
  let theShader;  // シェーダオブジェクト
  let capture;    // カメラのキャプチャ
  const scl = 0.8;

  p.preload = () => {
    theShader = p.loadShader(VERT_FILE, FRAG_FILE);
  };

  p.windowResized = () => {
    p.resizeCanvas(p.windowWidth * scl, p.windowHeight * scl, p.WEBGL);
  }

  p.setup = () => {
    // セットアップ処理
    p.createCanvas(400, 400);
  };

  p.draw = () => {
    p.background(220);
    p.ellipse(200, 200, 80, 80); // サークルを描画
  };
};

// p5.jsのスケッチコンポーネント
const SampleSketch = () => {
  useEffect(() => {
    new p5(sketch); // p5.jsのキャンバスを生成
  }, []);

  return (<></>);
};

export default SampleSketch;
