  return (
    <div className="CaptureContainer">

      <div className="CaptureContainerLeft">
        <img src={flensTypo}></img>
        <br></br>
        <img src={flensLogo}></img>
      </div>

      {/* カメラのキャプチャ */}
      <CameraCapture />

      <div className="CaptureContainerRight">
        <img src={flensTypo}></img>
      </div>

    </div>
  );
}
