import { useState, useEffect } from "react";
import CameraCapture from "./CameraCapture"
import "./style.css";

// 使用するGLSLフィルターのインデックス
export let shaderIndex = 0;

// Appコンポーネントの定義
function App() {
  // 次のシェーダをフィルターとして適用する
  function nextShader() {
    shaderIndex++;
  }

  // 前のシェーダをフィルターとして適用する
  function backShader() {
    shaderIndex--;
  }

  return (
    <div className="App">
      {/* ページのタイトル部分 */}
      <header>
        <h1>Capture Sketch</h1>
      </header>

      <main>
        {/* カメラのキャプチャ */}
        <CameraCapture />

        {/* UI */}
        <button onClick={backShader}>←</button>
        <button>o</button>
        <button onClick={nextShader}>→</button>
        <FileList />
      </main>

    </div>
  );
}

// Appコンポーネントを他のファイルで利用できるようにエクスポート
export default App;

function FileList() {
  const [files, setFiles] = useState([]);

  useEffect(() => {
    // ファイルのリストを取得する関数を呼び出し、その結果をセットする
    fetchFiles().then(data => setFiles(data));
  }, []);

  // ファイルのリストを取得する関数
  async function fetchFiles() {
    const fs = require('fs');
    const files = fs.readdirSync('ディレクトリへのパス');
    console.log(files)
    return ["file1.txt", "file2.txt", "file3.txt"];
  }

  return (
    <div>
      <h2>File List</h2>
      <ul>
        {files.map((file, index) => (
          <li key={index}>{file}</li>
        ))}
      </ul>
    </div>
  );
}
