import React, { Component } from "react";
import p5 from 'p5';

// シェーダリスト
import { filePathList } from "./filePathList";

// 利用するシェーダのインデックス
import { shaderIndex } from "../../src/App"

class SampleSketch extends Component {
  constructor(props) {
    super(props);
    this.sketchRef = React.createRef();
  }

  componentDidMount() {
    this.sketch = new p5(this.sketchHandler, this.sketchRef.current);
  }

  componentWillUnmount() {
    this.sketch.remove();
  }

  sketchHandler = p => {
    let theShader = []; // シェーダオブジェクト
    let capture;        // カメラのキャプチャ
    const scl = 0.8;

    p.preload = () => {
      filePathList.forEach(fp => {
        theShader.push(p.loadShader(fp["vert"], fp["frag"]));
      });
    };

    p.windowResized = () => {
      p.resizeCanvas(p.windowWidth * scl, p.windowHeight * scl, p.WEBGL);
    }

    p.setup = () => {
      // メインキャンバスの作成
      p.createCanvas(p.windowWidth * scl, p.windowHeight * scl, p.WEBGL);

      // 仮想キャンバスの作成
      // pg = p5.createGraphics(p5.width, p5.height);

      // Webカメラのキャプチャ開始
      capture = p.createCapture(p.VIDEO);
      capture.hide();
    };

    p.draw = () => {
      // shaderIndex = 0;
      // console.log();

      p.shader(theShader[shaderIndex]);

      theShader[shaderIndex].setUniform("u_time", p.frameCount / 100);
      theShader[shaderIndex].setUniform("u_Resolution", [p.width, p.height]);

      theShader[shaderIndex].setUniform("u_tex", capture);

      // theShader.setUniform('u_color0', normalizeColors[0]);
      // theShader.setUniform('u_color1', normalizeColors[1]);
      // theShader.setUniform('u_color2', normalizeColors[2]);
      // theShader.setUniform('u_color3', normalizeColors[3]);
      // theShader.setUniform('u_color4', normalizeColors[4]);

      p.rect(0, 0, p.width, p.height);
    };
  };

  render() {
    return <div ref={this.sketchRef}></div>;
  }
}

export default SampleSketch;
